mapper.select:
Select pNO,pName,pPrice,pCount,pInfo
from tr_product
where pNo=#{pNo}

mapper.update:
update TR_ORDER
	set DELIVERY=#{delivery}
where NO=#{no}

mapper.select:
Select no,name,addr,phone,count,oDate,delivery
from tr_order
where no=#{no}

SELECT qNo,qTitle,qWriter,qContent,qDate
FROM tr_qna
where qNo=#{qNo}

SELECT qNo,rNo,rContent,rWriter,rDate
FROM tr_reply
where qNo=#{qNo}

select qNo,rNo,rContent,rWriter,rDate
From tr_reply
where rNo=#{rNo}
