mapper.select:
 	select  substr(p.pno,1,1), p.pName, c.cateCode, c.cateName, pPrice, pImg, pInfo, p.pno
	 from tr_product  p, tr_category c
	       where  substr(p.pno,1,1) = c.cateCode and substr(p.pno,1,1) = #{cateCode}

select count(*)
from tr_cart
where userId= #{userId}
and pNo = #{pNo}

mapper.update:
update tr_cart
set count = count + #{count}
where userId= #{userId}
and pNo=#{pNo}
